var customerHighlightsMarkup = function ( customerHeroObj ) {
	let markupStart = `<section id="highlights" class="customer-story highlights"><div class="container"><div class="row">`;
  	let col = 12/customerHeroObj.companyHighlight.length;
  	for ( var x = 0; x < customerHeroObj.companyHighlight.length; x++ ) {
  		markupStart += `<div class="col-md-` + col + ` col-sm-6 col-xs-12"><h4>` + customerHeroObj.companyHighlight[x].title + `</h4><p>` + customerHeroObj.companyHighlight[x].highlight + `</p></div>`;
  	}
  	let markupFinish = `</div></div></section>`;
	return markupStart + markupFinish;
};

module.exports = customerHighlightsMarkup;