var customerQuotationMarkup = function ( customerHeroObj ) {
	let markupStart = `<section id="quotations" class="quotations"><div class="container-fluid"><div class="row"><div class="col-md-12"><div id="quotation-carousel" class="carousel slide" data-ride="carousel"><ol class="carousel-indicators">`;
  for ( let t = 0; t < customerHeroObj.companyQuote.length; t++ ) {
    if ( t === 0 ) {
      markupStart += `<li data-target="#quotation-carousel" data-slide-to="0" class="active"></li>`;
    } else if ( t + 1 !== customerHeroObj.companyQuote.length ) {
      markupStart += `<li data-target="#quotation-carousel" data-slide-to="` + t + `"></li>`;
    } else {
      markupStart += `<li data-target="#quotation-carousel" data-slide-to="` + t + `"></li></ol><div class="carousel-inner" role="listbox">`;
    }
  }
  for ( let x = 0; x < customerHeroObj.companyQuote.length; x++ ) {
    if ( x === 0 ) {
      markupStart += `<div class="item active"><div class="quotation"><div class="row"><div class="col-md-12 quote-content"><div class="blockquote"><h3>` + customerHeroObj.companyQuote[x].quote + `</h3></div><p><strong>` + customerHeroObj.companyQuote[x].name + `</strong><br>` + customerHeroObj.companyQuote[x].title + `</p></div></div></div></div>`;
    } else {
      markupStart += `<div class="item"><div class="quotation"><div class="row"><div class="col-md-12 quote-content"><div class="blockquote"><h3>` + customerHeroObj.companyQuote[x].quote + `</h3></div><p><strong>` + customerHeroObj.companyQuote[x].name + `</strong><br>` + customerHeroObj.companyQuote[x].title + `</p></div></div></div></div>`;
    }
  }
  let markupFinish = `</div></div></div></div></section>`;
	return markupStart + markupFinish;
};

module.exports = customerQuotationMarkup;