var customerSummaryMarkup = function ( customerHeroObj ) {

	let markupStart = `<section id="summary" class="customer-story summary"><div class="container">`;

	for ( var x = 0; x < customerHeroObj.companySummary.length; x++ ) {
		if ( x % 2 ) {
			markupStart += `<div class="col-md-6 col-sm-12"><h1>` + customerHeroObj.companySummary[x].title + `</h1><p>` + customerHeroObj.companySummary[x].summary + `</p></div></div>`;
		} else if ( x + 1 !== customerHeroObj.companySummary.length ) {
			markupStart += `<div class="row">
    <div class="col-md-6 col-sm-12"><h1>` + customerHeroObj.companySummary[x].title + `</h1><p>` + customerHeroObj.companySummary[x].summary + `</p></div>`;
		} else if ( x + 1 === customerHeroObj.companySummary.length ) {
			markupStart += `<div class="row">
    <div class="col-md-12 col-sm-12"><h1>` + customerHeroObj.companySummary[x].title + `</h1><p>` + customerHeroObj.companySummary[x].summary + `</p></div></div>`;
		}
	};

  markupStart += `<div class="row"><div class="col-md-12"><p style="text-align:center!important;"><a href="` + customerHeroObj.companyDocument + `" class="btn btn-primary" target="_blank">Read full story</a></p></div></div>`;
  let markupFinish = `</div></section>`;

	return markupStart + markupFinish;
};

module.exports = customerSummaryMarkup;