var customerVideosMarkup = function ( customerHeroObj ) {
	let markupStart = `<section id="videos" class="customer-story videos"><div class="container"><div class="row">`;
	for ( let x = 0; x < customerHeroObj.companyVideos.length; x++ ) {
		if ( customerHeroObj.companyVideos.length === 1 ) {
			markupStart += `<div class="col-md-6 col-sm-12 customer-video center"><div class="embed-responsive embed-responsive-16by9"><iframe class="embed-responsive-item" width="640" height="360" src="https://www.youtube.com/embed/` + customerHeroObj.companyVideos[x].id + `?rel=0&showinfo=0" frameborder="0" allowfullscreen></iframe></div><p>` + customerHeroObj.companyVideos[x].title + `</p><p style="margin:0 auto;"><a data-toggle="collapse" href="#video-first">Transcript <i class="fa fa-caret-down"></i></a></p><div class="collapse" id="video-` + customerHeroObj.companyVideos[x].id + `">` + customerHeroObj.companyVideos[x].transcript + `</div></div>`;
		} else {
			markupStart += `<div class="col-md-6 col-sm-12 customer-video"><div class="embed-responsive embed-responsive-16by9"><iframe class="embed-responsive-item" width="640" height="360" src="https://www.youtube.com/embed/` + customerHeroObj.companyVideos[x].id + `?rel=0&showinfo=0" frameborder="0" allowfullscreen></iframe></div><p>` + customerHeroObj.companyVideos[x].title + `</p><p style="margin:0 auto;"><a data-toggle="collapse" href="#video-first">Transcript <i class="fa fa-caret-down"></i></a></p><div class="collapse" id="video-` + customerHeroObj.companyVideos[x].id + `">` + customerHeroObj.companyVideos[x].transcript + `</div></div>`;
		}
	}
	let markupFinish = `</div></div></section>`;
	return markupStart + markupFinish;
};
module.exports = customerVideosMarkup;