let errHandling = ( property, type ) => {
	let returnValue;
	if ( type === 'date' && typeof property === 'object' ) {
		(typeof property[0] === 'undefined' ) ? returnValue = '' : returnValue = property[0];
	} else {
		(typeof property === 'undefined' ) ? returnValue = '' : returnValue = property;
	}
	return returnValue;
}

module.exports = errHandling;