const imgPathfromURL = require('./imgPathfromURL'),
	imgPathNamesObj = require('./imgPathNamesObj');

let imageFileNames = ( fileArr ) => {
	let newArr = [];
	for ( let x = 0; x < fileArr.length; x++ ) {
		let fileName = imgPathfromURL(fileArr[x]);
		let filePath = fileArr[x];
		let fileObj = new imgPathNamesObj(filePath,fileName);
		newArr.push(fileObj);
	}
	return newArr;
};

module.exports = imageFileNames;