let imgPathfromURL = ( str ) => {
	var reversal = str.split('');
	reversal = reversal.reverse();
	reversal = reversal.join('');
	reversal = reversal.replace(reversal.substr(reversal.indexOf('/'), reversal.length), '');
	reversal = reversal.split('');
	reversal = reversal.reverse();
	return reversal.join('');
};

module.exports = imgPathfromURL;