"use strict";

var imageModel = function (img, link, paragraph, present) {
	this.img = function () {
		if (typeof img !== "string") {
			return false;
		}
		return img;
	};
	this.link = function () {
		if (typeof link !== "string") {
			return false;
		}
		return link;
	};	
	this.paragraph = function() {		
		if (typeof paragraph !== "string") {
			return false;
		}
		return paragraph;
	};
	this.present = function () {
		if (typeof present !== "boolean") {
			return false;
		}
		return present;
	};
}

module.exports = imageModel;

